import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

export const ProfileContext = createContext();

export const ProfileProvider = ({ children }) => {
    const [profileImage, setProfileImage] = useState("./user.png");
    const [loading, setLoading] = useState(true);

    const refreshAccessToken = async () => {
        const refreshToken = localStorage.getItem("refreshToken");
        try {
            const response = await axios.post("http://localhost:3001/api/token/refresh", { token: refreshToken });
            const { accessToken } = response.data;
            localStorage.setItem("accessToken", accessToken);
            console.log("Access token refreshed successfully:", accessToken);
            return accessToken;
        } catch (error) {
            console.error("Error refreshing access token:", error);
            alert("Session expired. Please log in again.");
            //localStorage.removeItem("accessToken");
            //localStorage.removeItem("refreshToken");
            //window.location.href = "/signin";
            return null;
        }
    };
    const fetchProfileImage = async () => {
        const userId = localStorage.getItem("userId");
        let accessToken = localStorage.getItem("accessToken");
        console.log("Access Token being sent:", accessToken);

        if (!userId || !accessToken) {
            setLoading(false);
            return;
        }

        try {
            const response = await axios.get(`http://localhost:3001/api/user/profile?userId=${userId}`, {
                headers: {
                    Authorization: `Bearer ${accessToken}`,
                },
            });

            if (response.status === 403) {
                // Access Token 만료 시 Refresh
                console.log("Access Token expired, attempting to refresh...");
                accessToken = await refreshAccessToken();
                if (accessToken) {
                    // Refresh 후 재시도
                    const retryResponse = await axios.get(`http://localhost:3001/api/user/profile?userId=${userId}`, {
                        headers: {
                            Authorization: `Bearer ${accessToken}`,
                        },
                    });

                    if (retryResponse.ok) {
                        const data = await retryResponse.json();
                        if (data.img_src && data.img_src !== "") {
                            setProfileImage(data.img_src);
                        }
                    } else {
                        throw new Error("Failed to fetch profile image after token refresh");
                    }
                }
            } else if (response.ok) {
                const data = await response.json();
                if (data.img_src && data.img_src !== "") {
                    setProfileImage(data.img_src);
                }
            } else {
                throw new Error("Failed to fetch profile image");
            }
        } catch (error) {
            console.error("Error fetching profile image:", error);
            alert("Session expired. Please log in again.");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchProfileImage();
    });

    return (
        <ProfileContext.Provider value={{ profileImage, setProfileImage, loading }}>
            {children}
        </ProfileContext.Provider>
    );
};
